export declare const UButtonCSS = "\n  .btn {\n    background-color: #25262B;\n    border: 0;\n    padding: 1.25em;\n    border-radius: 4px;\n    cursor: pointer;\n    color: #ffffff;\n    font-weight: 600;\n    font-size: .75rem;\n    transition: background-color 0.2s ease-in-out;\n  }\n\n  .btn:hover {\n    background-color: #1A1B1E;\n  }\n\n  .btn:disabled {\n    background-color: #555;\n    cursor: not-allowed;\n    opacity: 0.6;\n  }\n\n  .btn.primary {\n    background-color: #007BFF;\n  }\n\n  .btn.primary:hover {\n    background-color: #0056b3;\n  }\n\n  .btn.secondary {\n    background-color: #6C757D;\n  }\n\n  .btn.secondary:hover {\n    background-color: #545B62;\n  }\n\n  .btn.sm {\n    padding: 0.5em;\n    font-size: 0.65rem;\n  }\n\n  .btn.md {\n    padding: 1em;\n    font-size: 0.85rem;\n  }\n\n  .btn.lg {\n    padding: 1.5em;\n    font-size: 1rem;\n  }\n\n  ::slotted(*) {\n    margin: 0;\n    padding: 0;\n    color: inherit;\n  }\n";
